    //sliding Window + Binary Search
    public static int smallestDistancePair(int[] nums, int k) {
        Arrays.sort(nums);
        int l = 0;
        int r = nums[nums.length - 1] - nums[0];
        int res = 0;
        while(l <= r){
            int mid = l +(r-l)/2;
            int noOfPair  = countPairLessThanOrEqualToMid(nums,mid);
            if(noOfPair < k){
                l = mid + 1;
            }else{
                res = mid;
                r = mid - 1;

            }
        }

        return res;
    }

    private static int countPairLessThanOrEqualToMid(int[] nums, int mid) {
        int n = nums.length;
        int i =0;
        int j=1;
        int count = 0;
        while(j<n){
            while(Math.abs(nums[i]-nums[j]) > mid){
                i++;
            }
            count += j-i;
            j++;
        }
        return count;
    }


